import React, { useState, useEffect } from "react";

// Define props for the EditWeight component
interface EditWeightProps {
  id: string;
  onClose: () => void;
}

const EditWeight: React.FC<EditWeightProps> = ({ id, onClose }) => {
  const [weight, setWeight] = useState<number | null>(null);

  useEffect(() => {
    const fetchWeight = async () => {
      try {
        const response = await fetch(`http://localhost:5000/weights/${id}`);
        const data = await response.json();
        setWeight(data.weight);
      } catch (error) {
        console.error("Error fetching weight:", error);
      }
    };

    fetchWeight();
  }, [id]);

  const handleUpdateWeight = async () => {
    if (weight === null) return;

    try {
      await fetch(`http://localhost:5000/weights/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ weight }),
      });

      onClose(); // Close modal after successful update
    } catch (error) {
      console.error("Error updating weight:", error);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white p-6 rounded-md shadow-md">
        <h2 className="text-xl font-bold mb-4">Edit Weight</h2>
        <input
          type="number"
          value={weight ?? ""}
          onChange={(e) => setWeight(Number(e.target.value))}
          className="w-full p-2 border rounded-md mb-4"
        />
        <div className="flex justify-end space-x-2">
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600"
          >
            Cancel
          </button>
          <button
            onClick={handleUpdateWeight}
            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
          >
            Update
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditWeight;
