import React, { useState } from "react";
import AddWeight from "./add_weight";


const Main: React.FC = () => {
    // Predefined weights
    const [weights, setWeights] = useState<number[]>([50, 75, 100]);
    const [showAddWeight, setShowAddWeight] = useState(false);

    // Function to add a new weight to the list
    const addWeight = (weight: number) => {
        setWeights((prevWeights) => [...prevWeights, weight]);
        setShowAddWeight(false);  // Hide the AddWeight component after adding
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-6">
            <h1 className="text-3xl font-bold mb-6">Weight List</h1>
            {/* List of predefined weights */}
            <ul className="space-y-2 mb-6">
                {weights.map((weight) => (
                    <li key={weight} className="text-lg text-gray-700">
                        Weight: {weight} kg
                    </li>
                ))}
            </ul>

            {/* Button to toggle AddWeight visibility */}
            <button
                onClick={() => setShowAddWeight((prev) => !prev)}
                className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-300"
            >
                {showAddWeight ? "Cancel" : "Add Weight"}
            </button>

            {/* Conditionally render AddWeight component */}
            {showAddWeight && <AddWeight onAdd={addWeight} />}
        </div>
    );
};

export default Main;
