import React, { useState, useEffect } from "react";

// Define the prop types for EditWeight component
interface EditWeightProps {
  id: string;
  onClose: () => void;
}

const EditWeight: React.FC<EditWeightProps> = ({ id, onClose }) => {
  const [weight, setWeight] = useState<number | null>(null);
  const [loading, setLoading] = useState<boolean>(false); // Loading state to manage API requests

  // Fetch weight details by ID when the component mounts or when `id` changes
  useEffect(() => {
    const fetchWeight = async () => {
      setLoading(true); // Start loading when making the request
      try {
        const response = await fetch(`http://localhost:5000/weights/${id}`);
        if (!response.ok) {
          throw new Error("Failed to fetch weight.");
        }
        const data = await response.json();
        setWeight(data.weight); // Set the weight if the response is successful
      } catch (error) {
        console.error("Error fetching weight:", error);
      } finally {
        setLoading(false); // End loading when the request finishes
      }
    };

    if (id) {
      fetchWeight(); // Trigger fetch when `id` is available
    }
  }, [id]); // Only rerun when `id` changes

  // Function to handle updating the weight
  const handleUpdateWeight = async () => {
    if (weight === null || loading) return; // Don't proceed if weight is null or loading

    setLoading(true); // Start loading before making the request

    try {
      const response = await fetch(`http://localhost:5000/weights/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ weight }), // Send updated weight
      });

      if (response.ok) {
        onClose(); // Close the modal if the update is successful
      } else {
        const error = await response.json();
        console.error("Failed to update weight:", error.message);
      }
    } catch (error) {
      console.error("Error updating weight:", error);
    } finally {
      setLoading(false); // End loading after the request finishes
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-sm">
        <h2 className="text-xl font-semibold mb-4 text-gray-800">Edit Weight</h2>
        <div className="mb-4">
          <label htmlFor="weight" className="block text-sm font-medium text-gray-700">
            Weight (kg)
          </label>
          <input
            id="weight"
            type="number"
            value={weight ?? ""}
            onChange={(e) => setWeight(Number(e.target.value))}
            className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter weight"
            disabled={loading} // Disable input while loading
          />
        </div>
        <div className="flex justify-end space-x-3">
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-200"
          >
            Cancel
          </button>
          <button
            onClick={handleUpdateWeight}
            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-300"
            disabled={loading} // Disable button while loading
          >
            {loading ? "Updating..." : "Update"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditWeight;
